<?php
/**
 * 01文字列をテキストに変換するサンプル
 */

function convert (string $string) :string {
    $length = 8;
    // 01文字列の形式チェック
    if (strlen($string) % $length > 0) {
        throw new Exception('01文字列の形式が不正です');
    }

    $converted = '';
    while (strlen($string) > 0) {
        $from = substr($string, 0, 8);

        // 変換対象文字を切り取り
        $string = substr($string, strlen($from));
        // 2進数から10進数に変換
        $to = bindec($from);

        // asciiから文字列取得
        $converted .= chr($to);
    }

    return $converted;
}

$string = '';

echo convert($string);
